# solution based on https://stackoverflow.com/questions/4576077/how-can-i-split-a-text-into-sentences
# vertaald naar Nederlands en rechtstekst specifieke uitdrukkingen toegevoeged

import re

alphabets = "([A-Za-z])"
prefixes = "(Stcrt|stcrt|Pb|pb|Mr|St|Mrs|Ms|Dr|mr|st|mrs|ms|dr|blz|Blz|alg|Alg|art|Art|betr|Betr|bv|Bv|bijv|Bijv|Br|br|ca|Ca|Cat|cat|cf|Cf|cfr|Cfr|dhr|Dhr|ed|Ed|fig|Fig|Hr|hr|id|Id|Incl|incl|Jhr|jhr|jkvr|Jkvr|Mej|mej|mr|Mr|mv|Mv|nr|Nr|Nrs|nrs|pg|Pg|Pag|pag|Art|art|prof|Prof|Pres|pres|Ref|ref|Resp|resp|st|St|tel|Tel|vgl|Vgl|vz|Vz|Wed|wed|zgn|Zgn|drs|Drs| ing|Ing)[.]"
suffixes = "(Inc|Ltd|Jr|Sr|Co)"
starters = "(Mr|Mrs|Ms|Dr)"
acronyms = "([A-Z][.][A-Z][.](?:[A-Z][.])?)"
websites = "[.](com|net|org|io|gov|be|nl|fr)"
point_point_point = "\(...\)"
only_numbers = re.compile("^[0-9]+[.]$")
only_point = re.compile("^\(...\)$")
numbers_with_points = "([0-9]+)[.]([0-9]+)"


def split_into_sentences(text):
    text = " " + text + "  "
    text = text.replace("\n", " ")
    text = re.sub(' +', ' ', re.sub('\t', ' ', text))
    text = re.sub(prefixes, "\\1<prd>", text)
    text = re.sub(websites, "<prd>\\1", text)
    text = re.sub(point_point_point, "(<prd><prd><prd>)", text)
    text = re.sub(numbers_with_points, "\\1<prd>\\2", text)
    text = re.sub(numbers_with_points, "\\1<prd>\\2", text)
    text = re.sub("\s" + alphabets + "[.] ", " \\1<prd> ", text)
    text = re.sub(acronyms + " " + starters, "\\1<stop> \\2", text)
    text = re.sub(alphabets + "[.]" + alphabets + "[.]" + alphabets + "[.]", "\\1<prd>\\2<prd>\\3<prd>", text)
    text = re.sub(alphabets + "[.]" + alphabets + "[.]", "\\1<prd>\\2<prd>", text)
    text = re.sub(" " + suffixes + "[.] " + starters, " \\1<stop> \\2", text)
    text = re.sub(" " + suffixes + "[.]", " \\1<prd>", text)
    text = re.sub(" " + alphabets + "[.]", " \\1<prd>", text)
    if "”" in text: text = text.replace(".”", "”.")
    if "\"" in text: text = text.replace(".\"", "\".")
    if "!" in text: text = text.replace("!\"", "\"!")
    if "?" in text: text = text.replace("?\"", "\"?")
    text = text.replace(".", ".<stop>")
    text = text.replace("?", "?<stop>")
    text = text.replace("!", "!<stop>")
    text = text.replace("<prd>", ".")
    sentences = text.split("<stop>")
    sentences = sentences[:-1]
    sentences = [s.strip() for s in sentences]
    sentences = [s for s in sentences if not only_numbers.match(s)]
    sentences = [s for s in sentences if not only_point.match(s)]
    return sentences


